import { useEffect, useState } from "react"
import { useNavigate, useParams } from "react-router-dom"
import { IProductItem, IProductOption } from "../../interfaces"
import { Endpoint, get, post } from "../../request/AppRequest"
import { Button, Image, InputNumber, Rate, Tag } from "antd"
import Number from "../../helper/Number"
import { ButtonWarning } from "../../custom/button"
import { useCartItem, useCheckoutContext } from "../../contexts/hooks"
import { CART_ACTION } from "../../contexts/CartContext"
import { toast } from "react-toastify"
import { CHECKOUT_ACTION } from "../../contexts/CheckoutContext"

type AddItemCartProps = {
    quantity: number,
    isSelected: boolean,
    productId: string,
    productOptionId?: string,
    orderId?: string,
    anonymous?: boolean
}

const ProductDetail: React.FC = () => {
    const { productId } = useParams()
    const [product, setProduct] = useState<IProductItem>()
    const { dispatchCartItems } = useCartItem()
    const { dispatchCheckoutItems } = useCheckoutContext()
    const [outStock, setOutStock] = useState(false)
    const [currentOption, setCurrentOption] = useState<IProductOption>()
    const [quantity, setQuantity] = useState(0)
    const navigate = useNavigate()

    const fetchProduct = async () => {
        const { status, data } = await get({
            url: `${Endpoint.PRODUCT_URL}/${productId}`
        })
        if (status === 200){
            setProduct(data)
            setQuantity(data?.productDetail.stock > 0 ? 1: 0)
        }else{
            toast.error(data)
        } 
    }

    useEffect(() => {
        fetchProduct()
    }, [productId])

    useEffect(() => {
        if (product?.productDetail && product?.productDetail.stock <= 0){
            setOutStock(true)
        }
    }, [product])

    const addCartItem = async (product: IProductItem) => {
        const props: AddItemCartProps = {
            isSelected: true,
            productId: product.id,
            quantity,
            productOptionId: currentOption?.id
        }
        try {
            const { data, status } = await post({
                url: `${Endpoint.CART_URL}`,
                payload: { ...props }
            })

            if (status === 200) {
                toast.success(`Product ${product.name} is added to cart (x${quantity})`)

                dispatchCartItems({
                    type: CART_ACTION.INIT,
                    payload: data.cartItems
                })
            } else {
                toast.error(data)
            }
        } catch (error) {
            if (error?.status === 401) {
                toast.error(`You need to login to use this function.`)
            } else {
                toast.error(`Bug2!`)
            }
        }
    }

    const handleBuyNow = async (product: IProductItem) => {
        const props: AddItemCartProps = {
            isSelected: true,
            productId: product.id,
            quantity,
            productOptionId: currentOption?.id,
            anonymous: true
        }
        try {
            const { data, status } = await post({
                url: `${Endpoint.CART_URL}`,
                payload: { ...props }
            })

            if (status === 200) {
                toast.success(`Product ${product.name} is added to checkout (x${quantity})`)

                dispatchCheckoutItems({
                    type: CHECKOUT_ACTION.INIT,
                    payload: [data]
                })

                setTimeout(() => {
                    navigate("/checkout")
                }, 200);
            } else {
                toast.error(data)
            }
        } catch (error) {
            if (error?.status === 401) {
                toast.error(`You need to login to use this function.`)
            } else {
                toast.error(`Bug2!`)
            }
        }
    }

    return <>
        <div className='ProductDetail flex flex-col items-center p-5 gap-2 animate-fade w-[100%]'>
            <div className="flex">
                <span className="flex flex-col">
                    <Image
                        key={product?.id}
                        width={320}
                        height={240}
                        src={product?.thumbnail?.url}
                        fallback="data:image/png;base64,"
                    />
                    <span className="flex gap-5 justify-center bg-[#f5f5f5] p-2">
                        {
                            product?.productDetail?.previews?.map(v => (<Image
                                key={product?.id}
                                width={60}
                                height={60}
                                src={v.url}
                                fallback="data:image/png;base64,"
                            />))
                        }
                    </span>
                </span>
                <div className="flex flex-col gap-2 text-black p-5">
                    <span className="font-bold text-4xl">{product?.name}</span>
                    <Rate disabled defaultValue={product?.productDetail?.totalRating} />
                    <span className="text-2xl">Price: <span className="font-bold text-red-400">{Number.formatPrice(product?.productDetail.price)}</span> <span className="line-through text-black text-xl">({Number.formatPrice(product?.productDetail.beforePrice)})</span> </span>
                    <span>Description: {product?.productDetail.description}</span>
                    <span>Quantity: <InputNumber min={0} max={product?.productDetail.stock} value={quantity} onChange={(val)=>setQuantity(val)}/></span>
                    <span>Options: {product?.productDetail?.productOptions.length && product?.productDetail?.productOptions.length > 0 && product?.productDetail?.productOptions?.map(v => (<Tag.CheckableTag onClick={()=>setCurrentOption(v)} checked={currentOption?.id === v.id} key={v.id}>{v.name}</Tag.CheckableTag>)) || <Tag.CheckableTag>No option</Tag.CheckableTag>}</span>
                    { outStock && <span className="text-red-400">Product is out of stock</span>}
                    <span className="flex gap-5 p-5">
                        <Button disabled={outStock} type="primary" size={'large'} onClick={() => { addCartItem(product!) }}>Add to cart</Button>
                        <ButtonWarning  disabled={outStock} type="primary" size={'large'} onClick={()=>handleBuyNow(product!)}>Buy now</ButtonWarning>
                    </span>
                </div>
            </div>
        </div>
    </>
}

export default ProductDetail